mapper.select:
SELECT * FROM TB_USER
WHERE USER_ID = #{user_id} AND USER_PW
= #{user_pw}

select user_id
from tb_user
where user_id = #{user_id}

select user_nick
from tb_user
where user_nick = #{user_nick}

select user_mail
from tb_user
where user_mail = #{user_mail}

mapper.update:
update tb_user
set user_pw
= #{user_pw}, user_name = #{user_name}, user_nick = #{user_nick},
user_mail = #{user_mail}
where user_id = #{user_id}
